import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from '../../environments/environment';

@Component({
  selector: 'app-person',
  templateUrl: './person.component.html',
  styleUrls: ['./person.component.css']
})
export class PersonComponent {
  person = { name: '', address: '' };

  constructor(private http: HttpClient) { }

  onSubmit(form: any): void {
    if (form.valid) {
      this.http.post(`${environment.apiBaseUrl}/api/person`, this.person, {
        headers: { 'Content-Type': 'application/json' }
      }).subscribe({
        next: (response) => {
          console.log('Person saved:', response);
          alert('Person information saved successfully!');
        },
        error: (error) => {
          console.error('Error saving person:', error);
          alert('Error saving person information.');
        }
      });
    }
  }
}
<div class="person-form">
  <h2>Person Information</h2>

  <form #personForm="ngForm" (ngSubmit)="onSubmit(personForm)">
    <div class="form-group">
      <label for="name">Name:</label>
      <input type="text"
             id="name"
             name="name"
             required
             [(ngModel)]="person.name"
             #name="ngModel"
             class="form-control" />
      <div *ngIf="name.invalid && (name.dirty || name.touched)" class="error">
        Name is required.
      </div>
    </div>

    <div class="form-group">
      <label for="address">Address (Optional):</label>
      <input type="text"
             id="address"
             name="address"
             [(ngModel)]="person.address"
             #address="ngModel"
             class="form-control" />
    </div>

    <button type="submit" [disabled]="personForm.invalid" class="btn btn-primary">
      Submit
    </button>
  </form>
</div>
